import React from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
} from "chart.js";
import { Bar } from "react-chartjs-2";
import datalabels from "chartjs-plugin-datalabels";

ChartJS.register(
  // CategoryScale,
  LinearScale,
  BarElement,
  // Title,
  // Tooltip,
  // Legend,
  datalabels
);

const options = {
  responsive: true,
  plugins: {
    datalabels: {
      display: true,
      color: ["#980505","#74829F","#6C7A99"],
      align: "top",
      anchor: "end",
      font: { size: "14" },
    },
  },
  scales: {
    
    y: {
      position: "left",
    
      ticks: {
        display:false,
        // The y-axis value will start from zero
        beginAtZero: true,
        max: 10,
        // stepSize: 10,
      },
      grid:{
        borderColor:'white',
        color:'white'
  
      },
      // scaleLabel: {
      //   display: true,
      //   labelString: "Y text",
      // },
    },
    
    x:{
        grid:{
          color:'white',
          borderColor:'white',
        },
       }
  },
};

const data = {
  
  labels: ["2027", "2028", "2029"],

  datasets: [
    {
      label: "Capacity",
      data: [8,7,10],
      backgroundColor: ["#980505","#74829F","#6C7A99"],
      barPercentage: .3,
      categoryPercentage: 0.5,
    },
  ],
};

export default function Adminbargraph() {
  return (
    <div style={{ maxWidth: "500px", maxHeight:"500px"}}>
      <Bar options={options} data={data} />
    </div>
  );
}
