import React from "react";
import "./Adminbargraph.css"
import { Chart as ChartJS } from "chart.js";
import { Bar } from "react-chartjs-2";
import datalabels from "chartjs-plugin-datalabels";
import {BsThreeDots} from "react-icons/bs";


export default function Adminbargraph() {
  return (
    <>
      <div className="adminbargraph">
        <div className="adminbargraph_row1">
          <label>Made Orders</label>
          <BsThreeDots style={{color:'#C3CAD9'}}/>
        </div>
        <div className="adminbargraph_row2">
          <Barchart/>
        </div>
      </div>
    </>
  );
}

function Barchart() {
  ChartJS.register(datalabels);

  const options = {
    // responsive: true,
    plugins: {
      datalabels: {
        display: true,
        color: ["#3361FF", "#29CC39", "#FF6633"],
        align: "top",
        anchor: "end",
        font: { size: "14" },
      },
    },
    scales: {
      y: {
        position: "left",

        ticks: {
          display: false,
          // The y-axis value will start from zero
          beginAtZero: true,
          max: 10,
        },
        grid: {
        color: "white",
          display:false,
          drawOnChartArea:false,
          borderWidth:0
        },
      },

      x: {
        grid: {
          display:false,
          drawOnChartArea:false,
          borderWidth:0
        },
        ticks:{
          color:'#7D8FB3'
        }
        
      },
    },
  };

  const data = {
    labels: ["2027", "2028", "2029"],
    // color:"#7D8FB3",

    datasets: [
      {
        label: "Capacity",
        data: [8, 7, 10],
        backgroundColor: ["#3361FF", "#29CC39", "#FF6633"],
        barPercentage: 0.3,
        borderRadius:3
        // categoryPercentage: 0.5,
      },
    ],
  };

  return (
    <div style={{ maxWidth: "400px" }}>
      <Bar options={options} data={data} />
    </div>
  );
}
