import React, { useState } from "react";
import "./styles/style.css";
import AddTask from "./addtask";
import EditTask from "./edittask";
import Boardaddtask from "./boardaddtask";
import Boardedittask from "./boardedittask";

export default function App() {
  //const [array, setArray] = useState([1, 2, 3, 1, 2, 3, 1, 2, 3, 1, 2, 3, 4]);

  const [sprintedit, setSprintEdit] = useState(false);
  const [sprintadd, setSprintAdd] = useState(false);
  const [addmodalshow, setAddModalShow] = useState(false);
  const [editshow, setEditShow] = useState(false);
  function handleAddClick(e) {
    e.preventDefault();
    setSprintAdd(sprintadd ? false : true);
  }
  function handleEditClick(e) {
    e.preventDefault();
    setSprintEdit(sprintedit ? false : true);
  }
  function handleBoardAddClick(e) {
    e.preventDefault();
    setAddModalShow(addmodalshow ? false : true);
  }
  function handleBoardEditClick(e) {
    e.preventDefault();
    setEditShow(editshow ? false : true);
  }
  return (
    <>
      <AddTask sprintadd={sprintadd} setSprintAdd={setSprintAdd} />
      <EditTask sprintedit={sprintedit} setSprintEdit={setSprintEdit} />
      <Boardaddtask
        addmodalshow={addmodalshow}
        setAddModalShow={setAddModalShow}
      />
      <Boardedittask editshow={editshow} setEditShow={setEditShow} />
      <div className="main_contentbar">
        <button
          onClick={(e) => {
            handleAddClick(e);
          }}
        >
          Add Task
        </button>
      </div>

      <div className="main_contentbar">
        <button
          onClick={(e) => {
            handleEditClick(e);
          }}
        >
          Edit Task
        </button>
      </div>
      <div className="main_contentbar">
        <button
          onClick={(e) => {
            handleBoardAddClick(e);
          }}
        >
          Board Add Task
        </button>
      </div>

      <div className="main_contentbar">
        <button
          onClick={(e) => {
            handleBoardEditClick(e);
          }}
        >
          Board Edit Task
        </button>
      </div>
    </>
  );
}
