import { useState } from "react";
import "./styles/style1.css";
import axios from "axios";
import { useEffect } from "react";
export default function Drageanddrop() {
  const [array, setArray] = useState([]);
  const [firstArray, setFirstArray] = useState([]);
  const [secondArray, setSecondArray] = useState([]);
  const [thirdArray, setThirdArray] = useState([]);
  const [fourthArray, setFourthArray] = useState([]);
  const [task, setTask] = useState([]);
  const [id, setId] = useState([]);
  const [dragElement, setDragElement] = useState({});
  useEffect((itm, indx) => {
    setId(id);
    setFirstArray([]);
    setSecondArray([]);
    setThirdArray([]);
    setFourthArray([]);

    var url =
      "https:pao5y2j7df.execute-api.us-west-1.amazonaws.com/usertaskfetch";
    var data = {};
    var header = {};

    axios
      .post(url, data, header)
      .then((res) => {
        setTask(res.data);
        var array = res.data;
        // console.log("arr" + JSON.stringify(array));
        var tempfirst = [];
        var tempsecond = [];
        var tempthird = [];
        var tempfourth = [];
        for (const element of array) {
          console.log("element" + JSON.stringify(element));
          if (element.txtStatus == "to-do") {
            tempfirst = [...tempfirst, element];
          } else if (element.txtStatus == "Inprogress") {
            tempsecond = [...tempsecond, element];
          } else if (element.txtStatus == "review") {
            tempthird = [...tempthird, element];
          } else if (element.txtStatus == "completed") {
            tempfourth = [...tempfourth, element];
          } else {
            console.log("No Task");
          }
        }
        setFirstArray(tempfirst);
        setSecondArray(tempsecond);
        setThirdArray(tempthird);
        setFourthArray(tempfourth);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const allowDrop = (e) => {
    e.preventDefault();
    // alert("hi");
  };
  const handleDrop = (e) => {
    // alert("hi");
    console.log(e.target.className);
    e.preventDefault();
    var target = e.target.className;

    var startedDiv = dragElement.startedDiv;
    var startedDiv = dragElement.startedDiv;
    if (
      target != startedDiv &&
      (e.target.className == "dnd_div1" ||
        e.target.className == "dnd_div2" ||
        e.target.className == " dnd_div3" ||
        e.target.className == "dnd_div4")
    ) {
      if (startedDiv == "dnd_div1") {
        delete firstArray[dragElement.index];
      } else if (startedDiv == "dnd_div2") {
        delete secondArray[dragElement.index];
      } else if (startedDiv == "dnd_div3") {
        delete thirdArray[dragElement.index];
      } else if (startedDiv == "dnd_div4") {
        delete fourthArray[dragElement.index];
      }
      if (target == "dnd_div1") {
        var temp = [...firstArray];
        console.log("dragElement1==>" + JSON.stringify(dragElement));
        temp.push(dragElement.item);
        setFirstArray(temp);
      } else if (target == "dnd_div2") {
        var temp = [...secondArray];
        console.log("dragElement2==>" + JSON.stringify(dragElement));
        temp.push(dragElement.item);
        setSecondArray(temp);
      } else if (target == "dnd_div3") {
        var temp = [...thirdArray];
        console.log("dragElement3==>" + JSON.stringify(dragElement));
        temp.push(dragElement.item);
        setThirdArray(temp);
      }
      if (target == "dnd_div4") {
        var temp = [...fourthArray];
        console.log("dragElement4==>" + JSON.stringify(dragElement));
        temp.push(dragElement.item);
        setFourthArray(temp);
      }
    }
  };
  const handleDrag = (e, index, startedDiv, item) => {
    console.log(startedDiv);
    console.log(index);
    setDragElement({ index: index, startedDiv: startedDiv, item: item });
    console.log(dragElement);
  };
  return (
    <div className="draganddrop">
      <div
        className="dnd_div1"
        onDragOver={(e) => allowDrop(e)}
        onDrop={(e) => handleDrop(e)}
      >
        {firstArray.map((item, index) => {
          return (
            <>
              <p
                className="dnd_div_body"
                draggable="true"
                onDragStart={(e) => {
                  handleDrag(e, index, "dnd_div1", item);
                }}
              >
                <label>{item.txtTitle}</label>
                <label>{item.txtUserName}</label>
              </p>
            </>
          );
        })}
      </div>
      <div
        className="dnd_div2"
        onDragOver={(e) => allowDrop(e)}
        onDrop={(e) => handleDrop(e)}
      >
        {secondArray.map((item, index) => {
          return (
            <>
              <div
                className="dnd_div_body"
                draggable="true"
                onDragStart={(e) => {
                  handleDrag(e, index, "dnd_div2", item);
                }}
              >
                <label>{item.txtTitle}</label>
                <label>{item.txtUserName}</label>
              </div>
            </>
          );
        })}
      </div>
      <div
        className="dnd_div3"
        onDragOver={(e) => allowDrop(e)}
        onDrop={(e) => handleDrop(e)}
      >
        {thirdArray.map((item, index) => {
          return (
            <>
              <div
                className="dnd_div_body"
                draggable="true"
                onDragStart={(e) => {
                  handleDrag(e, index, "dnd_div3", item);
                }}
              >
                <label>{item.txtTitle}</label>
                <label>{item.txtUserName}</label>
              </div>
            </>
          );
        })}
      </div>
      <div
        className="dnd_div4"
        onDragOver={(e) => allowDrop(e)}
        onDrop={(e) => handleDrop(e)}
      >
        {fourthArray.map((item, index) => {
          return (
            <>
              <div
                className="dnd_div_body"
                draggable="true"
                onDragStart={(e) => {
                  handleDrag(e, index, "dnd_div4", item);
                }}
              >
                <label>{item.txtTitle}</label>
                <label>{item.txtUserName}</label>
              </div>
            </>
          );
        })}
      </div>
    </div>
  );
}
