import React, { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import downarrow from "./images/downarrow.png";
import logo from "./images/snowlogo.png";
import menu from "./images/menu.png";
import "./styles/style2.css";
import search from "./images/search.png";
import cross from "./images/cross.png";
import add from "./images/add.png";
import { BsThreeDots } from "react-icons/bs";
import FullModal from "./fullmodal";
import Modal from "./modal";
import AddTask from "./addtask";
import EditTask from "./edittask";

export default function Apps() {
  const [array, setArray] = useState([1, 2, 3, 1, 2]);
  // 1, 2, 3, 1, 2, 3, 1, 2, 3
  const [show, setShow] = useState(false);
  const [sprintadd, setSprintAdd] = useState(false);
  const [epicshow, setEpicShow] = useState(false);
  const [modalshow, setModalShow] = useState(false);
  // const [addmodalshow, setAddModalShow] = useState(false);
  const [sprintedits, setSprintEdit] = useState(false);
  const [todotask, setTodoTask] = useState([]);
  const [sprintlist, setSprintList] = useState([]);
  var sid = "2";
  useEffect(() => {
    var req1 = {};
    var header1 = {};
    var url1 =
      "https://8gyk6qv545.execute-api.us-west-2.amazonaws.com/sprintfetch";
    axios
      .post(url1, req1, header1)
      .then((res) => {
        console.log(JSON.stringify(res.data));
        setSprintList(res.data);
      })
      .catch();

    var req2 = '{"id":" ' + sid + '"}';
    var header2 = {};
    var url2 =
      "https://335ze8cjo3.execute-api.us-west-2.amazonaws.com/todotaskfetch";
    axios
      .post(url2, req2, header2)
      .then((res) => {
        console.log("Hello==>", JSON.stringify(res.data));
        setTodoTask(res.data);
      })
      .catch();
    alert("fetched");
  }, []);
  function handleModalClick(e) {
    e.preventDefault();
    setModalShow(modalshow ? false : true);
  }
  function handleBoardAddClick(e) {
    e.preventDefault();
    setSprintAdd(sprintadd ? false : true);
  }
  function handleBoardEditClick(e) {
    e.preventDefault();
    // localStorage.setItem("sprint",item.id);
    // console.log("task==>",item.id)
    setSprintEdit(sprintedits ? false : true);
  }

  return (
    <>
      <FullModal show={show} setShow={setShow} />
      <Modal modalshow={modalshow} setModalShow={setModalShow} />
      <AddTask sprintadd={sprintadd} setSprintAdd={setSprintAdd} />
      <EditTask sprintedits={sprintedits} setSprintEdit={setSprintEdit} />
      <div className="main">
        <div className="main_menunbar">
          <img src={logo} />
          <div className="main_menunbar_menu">
            <ul>
              <li>
                <label>Board</label>
                <div>
                  <img src={downarrow} />
                </div>
              </li>
              <li>
                <label>Board</label>
                <div>
                  <img src={downarrow} />
                </div>
              </li>{" "}
              <li>
                <label>Board</label>
                <div>
                  <img src={downarrow} />
                </div>
              </li>{" "}
              <li>
                <label>Board</label>
                <div>
                  <img src={downarrow} />
                </div>
              </li>{" "}
              <li>
                <label>Board</label>
                <div>
                  <img src={downarrow} />
                </div>
              </li>
            </ul>
          </div>
        </div>
        <div className="main_contentbar">
          <button
            onClick={(e) => {
              handleModalClick(e);
            }}
            className="fab_button"
          >
            +
          </button>
          <div className="main_contentbar_headerbar">
            <div className="main_contentbar_headerbar_left">
              <div>
                <img src={menu} />
              </div>
              <label>Sprint</label>
            </div>
            <div className="main_contentbar_headerbar_right">
              <div className="circle">
                <label>AB</label>
              </div>
            </div>
          </div>
          <div className="main_contentbar_search">
            <div className="main_contentbar_search_left">
              <img src={search} />
              <input placeholder="Search by order #, name or email..." />
            </div>
            <div className="main_contentbar_search_right">
              <button>
                Filters <img src={downarrow} />
              </button>
            </div>
          </div>
          <div className="main_contentbar_filters">
            <div className="main_contentbar_filters_inner">
              <button>
                Filters{" "}
                <img
                  className="main_contentbar_filters_inner_img"
                  src={cross}
                />
              </button>
            </div>
            <div className="main_contentbar_filters_inner">
              <button>
                <img className="main_contentbar_filters_inner_add" src={add} />
              </button>
            </div>
          </div>
          <div className="main_contentbar_list">
            <div className="table_head">
              <label></label>
              <label>Sprint Name</label>
              <label> Start Date</label>
              <label>End Date</label>
              <label>Task</label>
              <label>Assigned To</label>
              <label></label>
            </div>
            {/* {sprintlist.map((item,index)=>{
                    return(
                      <>
                      
                      <tr>
                        <td>{item.txtSprintName}</td>
                        <td>{item.DtActStartDate}</td>
                        <td>{item.DtActEndDate}</td>
                        <td>{item.txtTitle}</td>
                        <td>{item.txtUserName}</td>
                      </tr>
                      
                      </>
                      
                      )
                   })};  */}
            {sprintlist.map((itm, index) => {
              return (
                <div className={itm == 2 ? "row_full active" : "row_full"}>
                  <div className="table_row">
                    <div className="table_row1" onClick={(e) => {
                          setEpicShow(epicshow ? false : true);
                          // ;listepic(itm.id, index);
                        }}>
                      <input type={"checkbox"} />
                      <label className="labsprint">
                        {itm.txtSprintName}
                      </label>

                      <label className="labAdate">{itm.dtActStartDate}</label>
                      <label className="labEdate">{itm.dtActEndtDate}</label>
                      <label className="labStask">{itm.txtTitle}</label>
                      <label className="labAssignee">{itm.txtUserName}</label>
                    </div>
                    <div className="dropdown">
                      <label>
                        <BsThreeDots className="dropbtn" />
                      </label>
                      <div className="dropdown-content">
                        <label onClick={(e) => handleBoardEditClick(e)}>
                          Edit
                        </label>
                        <label style={{ color: "gray" }}>Delete</label>
                      </div>
                    </div>
                  </div>
                  <div className={epicshow == true ? "visible" : "hidden"}>
                    <div className="epic_row">
                      <label className="epic_rowlab">task123</label>
                      {/* {epiclist.map((eitem, index) => {
                        return (
                          <>
                            <label></label>
                            <label>{eitem.txtTitle}</label>
                            <label>{eitem.txtStatus}</label>
                            <label>{eitem.txtUserName}</label>
                            <label>
                              <BsThreeDots />
                            </label>{" "}
                          </>
                        );
                      })} */}
                    </div>
                    <div className="buttonrow">
                      <button
                        onClick={(e) => {
                          handleBoardAddClick(e);
                        }}
                      >
                        + Add Task
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
            {/* {/
                   
              );
            })} */}
          </div>

          <div>
            {" "}
            <p>
              <label className="headingbacklog">Backlog</label>
            </p>
          </div>
          <div className="main_contentbar_list2">
            <div className="table_head2">
              <label></label>
              <label>Task</label>
              <label>Status</label>
              <label> Sprint Name</label>
              <label>Assigned to</label>
              <label></label>
            </div>
            <div className="row_full"></div>
            <div className="table_row2">
              {/* {todotask.map((item,index)=>{
                    return(
                      <>
                      
                      <tr>
                      <td><input type={"checkbox"} /></td>
                        <td>{item.txtTitle}</td>
                        <td>{item.txtSprintName}</td>
                        <td>{item.txtUserName}</td>
                        
                      </tr>
                      
                      </>
                      
                      )
                   })}; */}
              {todotask.map((item, index) => {
                return (
                  <>
                    <input type={"checkbox"} />
                    <label className="labeltask1">{item.txtTitle}</label>
                    <label className="labeltodo">To Do</label>
                    <label className="labelsprint">{item.txtSprintName}</label>
                    <label className="labelname">{item.txtUserName}</label>
                  </>
                );
              })}
              <div className="dropdown">
                <BsThreeDots className="dropbtn" />
                <div className="dropdown-content">
                  <label onClick={(e) => handleBoardEditClick(e)}>Edit</label>
                  <label style={{ color: "gray" }}>Delete</label>
                </div>
              </div>
              {/* <div className="table_row"> */}
              {/* {todotask.map((item,index)=>{
                    return(
                      <>
                      
                      <tr>
                      <td><input type={"checkbox"} /></td>
                        <td>{item.txtTitle}</td>
                        <td>{item.txtSprintName}</td>
                        <td>{item.txtUserName}</td>
                        
                      </tr>
                      
                      </>
                      
                      )
                   })}; */}
              {/* </div> */}
            </div>
            {/* </div> */}
          </div>
        </div>
      </div>
    </>
  );
}
